import ICON from "../../../Icons/plus-svgrepo-com.svg";

export default function Form( {setTodos}){
const handleSubmit = (event) =>{
    event.preventDefault(); // Prevent submit before the form loads.
    const value = event.target.todo.value;
    setTodos((prevTodos) => [...prevTodos,{title: value ,id: crypto.randomUUID, is_completed: false},
    ]);
    event.target.reset(); // Resetting input field.
};

    return(
        <form className="form" onSubmit={handleSubmit}>
            <label htmlFor="todo">
                <input type="text" name="todo" placeholder="Add Your Tasks Here..."/>
            </label>
            <button>
                <span className="submit-btn"></span>
                <img className="add-icon" src ={ICON}/>
            </button>
        </form>
    );
}
